import React, {useState, useEffect} from 'react'
import {Container, Col, Image, Row, Card, Button} from "react-bootstrap"
import bigStar from '../assets/bigStar.png'
import {useParams} from 'react-router-dom'
import { fetchOneDevice, addToBasket } from '../http/deviceAPI'

const DevicePage = () => {
  const [device, setDevice] = useState({info: []})
  const {id} = useParams();
  useEffect(()=>{
    fetchOneDevice(id).then(data=>setDevice(data))
  }, [])

  const add = () => {
    const formData = new FormData()
    formData.append('deviceId', id)
    addToBasket(formData).then(response => alert(`Товар ` + device.name + ` был добавлен в вашу корзину!`))
}

  return (
    <div>
        <Container className="mt-3">
        <Row>
        <Col md={4}>
            <Image width={300} height={300} src={process.env.REACT_APP_API_URL + device.img}/>
          </Col>
          <Col md={4}>
            <Row className="d-flex flex-column align-items-center">
                <h2 className="d-flex justify-content-center">{device.name}</h2>
                <div
                    className="d-flex align-items-center justify-content-center"
                    style={{background: `url(${bigStar}) no-repeat center center`, width:240, height:240, backgroundSize: 'cover', fontSize:64}}
                >
                  {/* {device.rating} */}0
                </div>
            </Row>
          </Col>
          {/* <Col md={4}>
            <Card
              className="d-flex flex-column align-items-center justify-content-around"
              style={{width:300, height:300, fontSize:32, border: '5px solid lightgray'}}
            >
              <h3>от: {device.price} грн.</h3>
              <Button variant="outline-dark" onClick={add}>Добавить в корзину</Button>
            </Card>
          </Col> */}
        </Row>
          {/* <Row className="d-flex flex-column m-3">
          <h1>Характеристики</h1>
            {device.info.map((info, index) => 
              <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                {info.title}: {info.description}
              </Row>
            )}
          </Row> */}
        </Container>
    </div>
    );
};

export default DevicePage;
