// export default NavBar

import React, {useContext, useTransition} from 'react'
import {Context} from '../index'
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import NavLink from 'react-bootstrap/esm/NavLink'
import {SHOP_ROUTE, BASKET_ROUTE, ADMIN_ROUTE, LOGIN_ROUTE} from '../utils/consts'
import {Button} from 'react-bootstrap'
import { observer } from 'mobx-react-lite'
import { Container } from 'react-bootstrap'
import {useNavigate} from "react-router-dom"

const NavBar = observer(() => {
    const {user} = useContext(Context)
    const navigate = useNavigate()

    const logOut = () => {
        user.setUser({})
        user.setIsAuth(false)
        user.setIsAdmin(false)
    }

    return (
        <Navbar bg="dark" variant="dark">
         <Container>
         <NavLink className="d-flex row align-items-center"  style={{color:'black'}} to={SHOP_ROUTE}>Luzin.app</NavLink>
                {user.isAuth ?
                    <Nav className="ml-auto" style={{color: 'black'}}>
                        <Button
                         variant={"outline-dark"}
                            className="mr-2"
                            onClick={() => navigate(BASKET_ROUTE)}
                        >
                         Корзина
                        </Button>
                        {user.isAdmin ?
                            <Button
                                variant={"outline-dark"}
                                onClick={() => navigate(ADMIN_ROUTE)}
                            >
                                Админ панель
                            </Button>
                            :
                            <div></div>
                        }
                        <Button
                        variant={"outline-dark"}
                            onClick={() => logOut()}
                            className="ml-2"
                        >
                            Выйти
                        </Button>
                    </Nav>
                    :
                    <Nav className="ml-auto" style={{color: 'white'}}>
                        <Button variant={"outline-light"} onClick={() => navigate(LOGIN_ROUTE)}>Авторизация</Button>
                    </Nav>
                }
            </Container>
        </Navbar>
    );
});
export default NavBar;
