import React, {useContext} from 'react'
import {Context} from '../index'
import {SHOP_ROUTE, BASKET_ROUTE, ADMIN_ROUTE, LOGIN_ROUTE} from '../utils/consts'
import {Button} from 'react-bootstrap'
import { observer } from 'mobx-react-lite'
import { Container } from 'react-bootstrap'
import {useNavigate} from "react-router-dom"
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';

const NavBar = observer(() => {
    const {user} = useContext(Context)
    const navigate = useNavigate()

    const logOut = () => {
        user.setUser({})
        user.setIsAuth(false)
    }

    return (
        <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
      <Container>
        <Navbar.Brand style={{color:'white'}} onClick={()=>navigate(SHOP_ROUTE)}>MogilniyShop</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
        {user.isAuth ? 
                <Nav className="me-auto">
                    <Button                       
                        
                        onClick={()=> {                    
                            navigate(ADMIN_ROUTE)                           
                        }}
                    >
                        Админ панель
                    </Button>
                    <Button
                            
                            className="mr-2"
                            onClick={() => navigate(BASKET_ROUTE)}
                        >
                            Корзина
                        </Button>
                    <Button 
                        variant={"outline-dark"} 
                        onClick={()=> logOut()}
                        className="ms-2"
                    >
                        Выйти
                    </Button>
                </Nav>
                :
                <div style={{color: 'black'}}>
                    <Button variant={"outline-dark"} onClick={()=>{
                        navigate(LOGIN_ROUTE)
                        }}>Авторизация</Button>
                </div>
            }

          {/* <Nav className="me-auto">
            <Nav.Link href="#features">Features</Nav.Link>
            <Nav.Link href="#pricing">Pricing</Nav.Link>
          </Nav> */}
          <Nav>
            <Nav.Link href="#deets">More deets</Nav.Link>
            <Nav.Link eventKey={2} href="#memes">
              Dank memes
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
        // <div>
        // <Container>
        //  <div style={{color:'black'}} onClick={()=>navigate(SHOP_ROUTE)}>MogilniyShop</div>
        //  {user.isAuth ? 
        //          <div style={{color: 'black'}}>
        //              <Button                       
        //                  variant={"outline-dark"}
        //                  onClick={()=> {                    
        //                      navigate(ADMIN_ROUTE)                           
        //                  }}
        //              >
        //                  Админ панель
        //              </Button>
        //              <Button
        //                      variant={"outline-dark"}
        //                      className="mr-2"
        //                      onClick={() => navigate(BASKET_ROUTE)}
        //                  >
        //                      Корзина
        //                  </Button>
        //              <Button 
        //                  variant={"outline-dark"} 
        //                  onClick={()=> logOut()}
        //                  className="ms-2"
        //              >
        //                  Выйти
        //              </Button>
        //          </div>
        //          :
        //          <div style={{color: 'black'}}>
        //              <Button variant={"outline-dark"} onClick={()=>{
        //                  navigate(LOGIN_ROUTE)
        //                  }}>Авторизация</Button>
        //          </div>
        //      }
        //     </Container>
        // </div>
    );
});
export default NavBar;
